$(document).ready(function(){const a=$("#cart");let cartOffsetTop=a.offset().top;if($(window).width()<768){a.find("button").on("click",function(){a.find(".dropdown-menu li div").css({"min-width":($(window).width()-30)})})}$(window).on("scroll",function(){if($(window).scrollTop()>=cartOffsetTop){a.parent().addClass("sticky-cart")}else{a.parent().removeClass("sticky-cart")}})});