$(document).ready(function(){if($(window).width()<768){var a=$("#cart").height();$("#cart").on("click",function(){$("#cart").parent().toggleClass(" sticky-cart-open")})}var b=$("#cart").offset().top;$(window).on("scroll",function(){if($(window).scrollTop()>=b){$("#cart").parent().addClass(" sticky-cart");if($(window).width()<768){$("header").css("padding-bottom",a)}}else{$("#cart").parent().removeClass(" sticky-cart");if($(window).width()<768){$("header").css("padding-bottom",0)}}})});